pragma solidity ^0.6;


interface ISmoothy {

    function getBalance (
        uint256 tid
    )
        external
        view
        returns (uint256 balance);

    function _yBalances (
        uint256 tid
    )
        external
        view
        returns (uint256 balance);

    function getTokenStats (
        uint256 tid
    )
        external
        view
        returns (uint256 softWeight, uint256 hardWeight, uint256 balance, uint256 decimals);
}

